mapper.select:


     and sm.menu_name like concat(concat('%', #{menuName}), '%')
     and sm.component_name like concat(concat('%', #{componentName}), '%')
     and sm.visible = #{visible}
     and sm.status = #{status}

order by parent_id, order_num

select sm.menu_id, sm.parent_id, sm.menu_name, sm.path, sm.component, sm.query_param, sm.visible, sm.status, sm.perms, sm.is_frame, sm.is_cache, sm.menu_type, sm.icon, sm.hidden_expression, sm.shop_expression, sm.order_num, sm.create_time from sys_menu sm
EXISTS (SELECT 1 FROM sys_role_menu rm JOIN sys_user_role sur ON rm.role_id = sur.role_id JOIN sys_role ro ON sur.role_id = ro.role_id WHERE sur.user_id = #{userId} AND ro.status = '1' AND ro.del_flag = '0' AND sm.menu_id = rm.menu_id ) and sm.menu_name like concat(concat('%', #{menuName}), '%') and sm.component_name like concat(concat('%', #{componentName}), '%') and sm.visible = #{visible} and sm.status = #{status}
order by parent_id, order_num

select m.menu_id from sys_menu m left join sys_role_menu rm on m.menu_id = rm.menu_id where rm.role_id = #{roleId}
and m.menu_id not in (select m.parent_id from sys_menu m inner join sys_role_menu rm on m.menu_id = rm.menu_id and rm.role_id = #{roleId})
order by m.parent_id, m.order_num
