mapper.select:


     and sp.post_code like concat(concat('%', #{postCode}), '%')
     and sp.post_category like concat(concat('%', #{postCategory}), '%')
     and sp.post_name like concat(concat('%', #{postName}), '%')
     and sp.status = #{status}
     and sp.dept_id = #{deptId}

        #{item}


order by create_time desc
